foreach ($ip_list as $index => $item) {
    $isMatch = true;
    foreach ($keyArr as $key) {
        if (!empty($key) && strpos($item['module'], $key) === FALSE && strpos($item['tag'], $key) === FALSE) {
            $isMatch = false;
            break;
        }
    }
    $ipArr = explode("\n", $item['ip']);
